    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>s-card</span> <span class="token attr-name">:bodyStyle</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>{
        boxSizing: <span class="token punctuation">'</span>border-box<span class="token punctuation">'</span>,
        width: typeof width === <span class="token punctuation">'</span>number<span class="token punctuation">'</span>?width+<span class="token punctuation">'</span>px<span class="token punctuation">'</span>:width,
