<span class="token keyword">import</span> <span class="token punctuation">{</span>infoPanelProps<span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">'./info-panel'</span>
<span class="token keyword">import</span> <span class="token punctuation">{</span> ComponentConstants <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">'../../../core/constants'</span><span class="token punctuation">;</span> 
