          <span class="token keyword">return</span> ComponentConstants<span class="token punctuation">.</span>ThemeColorMap<span class="token punctuation">.</span><span class="token function">get</span><span class="token punctuation">(</span>props<span class="token punctuation">.</span>theme<span class="token operator">+</span><span class="token string">'-dark'</span><span class="token punctuation">)</span>
